import React,{useState} from 'react'
import { azkary} from './DataList'

const TotalZekr = () => {
  
    const [selectedId, setSelectedId] = useState(null);

  const handleButtonClick = (id) => {
    setSelectedId(id);
  };

const selectedObject = azkary.find((obj) => obj.id === selectedId);

  return (
    <div>
      {azkary.map((item) => (
        <div className="d-flex border-bottom mx-3 my-1">
          <div>
            <p className="fs-6">{item.zekr}</p>
            <p className="d-inline fs-3">{item.repeat}</p>
            <button key={obj.id} onClick={() => handleButtonClick(obj.id)}>
              {obj.name}
            </button>
            <h4 className="bless">{item.bless}</h4>
          </div>
        </div>
      ))}
    </div>
  );
}

export default TotalZekr
