import React from 'react'
import { content } from './DataList';
import { azkary } from './DataList';
import { Azkar } from "./DataList";



// function ContentList({content }) {
  





const TotalZekr = (content) => {

const handleClick = (repeat) => {
    // perform action when paragraph is clicked with the given repeat value
    console.log(`Clicked paragraph with repeat value ${repeat}`);
  };

  return (
    <div>
      {content.map((item, index) => (
        <p key={index} onClick={() => handleClick(item.repeat)}>
          {item.zekr}
        </p>
      ))}
    </div>
  );
}

export default TotalZekr
