import React, { useState } from "react";
import { Azkar } from "./DataList";
import "./NewList.css"; // Import a CSS file with the styling for the clicked paragraph

const NewList = () => {
  const azk = Azkar.content;
  const [clickedIndex, setClickedIndex] = useState(-1);

  function handleClick(index) {
    setClickedIndex(index);
  }

  return (
    <div>
      {azk.map((item, index) => (
        <div className="d-flex border-bottom mx-3 my-1" key={index}>
          <div>
            <p
              onClick={() => handleClick(index)}
              className={`fs-6 ${clickedIndex === index ? "clicked" : ""}`}
            >
              {item.zekr}
            </p>
            <p className="d-inline fs-3">{item.repeat}</p>
            <h4 className="bless">{item.bless}</h4>
          </div>
        </div>
      ))}
    </div>
  );
};

export default NewList;
