import React from "react";
import { Azkar } from "./DataList";

console.log(Azkar.content);

function ContentList({ content }) {
  function handleClick(repeat, clickedRepeat) {
    if (parseInt(repeat) === parseInt(clickedRepeat)) {
      console.log(`Clicked paragraph ${repeat}`);
    }
  }

  return (
    <div>
      {content.map((item, index) => (
        <p key={index} onClick={() => handleClick(item.repeat, index)}>
          {item.zekr}
          {typeof(item.repeat)}
        </p>
      ))}
    </div>
  );
}

export default ContentList;
