import React, { useState, useEffect } from "react";

const Phot = () => {
  const [azk, setData] = useState([]);

  useEffect(() => {
    fetch("https://ahegazy.github.io/muslimKit/json/azkar_massa.json")
      .then((response) => response.json())
      .then((data) => {
        data.content.map(i=>console.log(i.zekr))
        const azk = data.content
        setData(azk)});
  }, []);

  return (
    <div>
      {azk.map((item) => (
        <div className="d-flex border-bottom mx-3 my-1">
          {/* <img className="img-avatar" src={item.url} alt="fgfhg" /> */}
          <div className="px-5">
            <p className="d-inline fs-3">{item.title}</p>
            <p className="fs-6">{item.zekr}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Phot;
