import { Button, Col, Container, Row } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

function App() {

  const person = [
    // {id:'1', name:'احمد',date:'4 مساء',img:'12.jpg'},
    // {id:'1', name:'عصام',date:'9 مساء',img:'22.png'},
    // {id:'1', name:'علاء',date:'7 مساء',img:'12.jpg'},
    // {id:'1', name:'محمود',date:'5 مساء',img:'12.png'},
    // {id:'1', name:'وليد',date:'1 مساء',img:'12.jpg'},
    // {id:'1', name:'عبدالله',date:'2 مساء',img:'22.png'},
  ]



  return (
    <div className="font color-body">
      <Container className="py-5">
        <Row className="justify-content-center ">
          <Col sm="8" className="">
            لديك {person.length} مواعيد اليوم
          </Col>
        </Row>

        <Row className="justify-content-center my-2">
          <Col sm="8" className="">
            <div className="rectangle p-2">

              {(person.length>0) ? (person.map(per=>{
                return (
                  <div className="d-flex border-bottom mx-3 my-2">
                    <img className="img-avatar" src={per.img} alt="fgfhg" />
                    <div className="px-3">
                      <p className="d-inline fs-3">{per.name}</p>
                      <p className="fs-6">{per.date}</p>
                    </div>
                  </div>
                );
                })):(<h2>لا يوجد بيانات</h2>)}
            </div>
          </Col>
        </Row>

        <Row className="justify-content-center my-2 ">
          <Col sm="8" className="d-flex justify-content-between">
            <button className="btn-style">مسح الكل</button>
            <button className="btn-style">عرض الكل</button>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default App;
